INSERT INTO article (title, content) VALUES ('스프링 부트란 무엇인가?', '스프링 부트는 스프링 프레임워크를 기반으로 한 경량화된 애플리케이션 개발 프레임워크입니다. 빠르게 프로덕션급 애플리케이션을 개발할 수 있도록 돕는 다양한 기능들을 제공합니다.');
INSERT INTO article (title, content) VALUES ('스프링 부트 시작하기: 기본 설정', '스프링 부트 프로젝트를 시작하는 방법을 설명합니다. 스프링 이니셜라이저를 사용하여 프로젝트를 생성하고, 기본적인 의존성을 설정하는 과정을 다룹니다.');
INSERT INTO article (title, content) VALUES ('스프링 부트와 JPA로 데이터베이스 연결하기', '스프링 부트에서 JPA(Hibernate)를 사용하여 데이터베이스에 연결하고, CRUD 연산을 수행하는 방법을 소개합니다. Entity 클래스와 Repository 인터페이스의 구현 방법도 함께 설명합니다.');
INSERT INTO article (title, content) VALUES ('스프링 부트로 REST API 개발하기', '스프링 부트를 사용하여 RESTful API를 설계하고 개발하는 방법을 다룹니다. 컨트롤러와 서비스 계층을 구현하여 클라이언트 요청을 처리하는 방법을 설명합니다.');
INSERT INTO article (title, content) VALUES ('스프링 부트의 의존성 주입(DI) 이해하기', '스프링 부트에서 의존성 주입(DI)이 어떻게 작동하는지 이해하고, 이를 활용하여 코드의 결합도를 낮추는 방법을 설명합니다. @Autowired, @Component 어노테이션의 사용법도 함께 다룹니다.');
INSERT INTO article (title, content) VALUES ('스프링 부트의 자동 구성(Autoconfiguration) 이해하기', '스프링 부트의 자동 구성 기능을 이해하고, 이를 통해 개발자가 빠르게 애플리케이션을 구성할 수 있는 방법을 설명합니다. 다양한 설정 옵션과 커스터마이징 방법도 다룹니다.');
INSERT INTO article (title, content) VALUES ('스프링 부트 보안: Spring Security 적용하기', '스프링 부트 애플리케이션에 Spring Security를 적용하여 보안을 강화하는 방법을 설명합니다. 사용자 인증과 권한 부여의 기본 개념을 다룹니다.');
INSERT INTO article (title, content) VALUES ('스프링 부트 애플리케이션 모니터링하기', '스프링 부트의 Actuator를 사용하여 애플리케이션의 상태와 메트릭스를 모니터링하는 방법을 소개합니다. 실시간으로 애플리케이션의 상태를 추적하는 데 유용한 정보를 제공합니다.');
INSERT INTO article (title, content) VALUES ('스프링 부트와 클라우드: 클라우드 네이티브 애플리케이션 개발', '스프링 부트를 사용하여 클라우드 네이티브 애플리케이션을 개발하는 방법을 설명합니다. 클라우드 환경에 애플리케이션을 배포하고 관리하는 다양한 전략을 다룹니다.');
INSERT INTO article (title, content) VALUES ('스프링 부트의 테스트 전략', '스프링 부트 애플리케이션의 품질을 보장하기 위한 다양한 테스트 전략을 소개합니다. 단위 테스트, 통합 테스트, Mocking 기법 등을 활용하여 테스트를 효과적으로 수행하는 방법을 설명합니다.');;
